mapper.select:
SELECT seat
FROM ci_reserv
WHERE movie_id = #{id}

SELECT re.movie_id
FROM ci_reserv re
WHERE re.id = #{param1}

SELECT re.user_id
FROM ci_reserv re
WHERE re.id = #{param1}

	select count(re.seat) as seatCount
    from ci_reserv re,
         ci_user u,
         ci_movies m
    WHERE re.user_id = u.id
      AND re.movie_id = m.id
      AND re.user_id = #{param1}
      AND m.id = #{param2}
